mapper.select:


     and id = #{id}
     and name like concat('%', #{name}, '%')
     and phone = #{phone}
     and channel = #{channel}
     and activity_id = #{activityId}
     and sex = #{sex}
     and age = #{age}
     and weixin = #{weixin}
     and qq = #{qq}
     and level = #{level}
     and subject = #{subject}

        and date_format(create_time,'%y%m%d') >= date_format(#{params.beginCreateTime},'%y%m%d')


        and date_format(create_time,'%y%m%d') <= date_format(#{params.endCreateTime},'%y%m%d')

     and next_time = #{nextTime}
    and status in ('3','4')

order by create_time



     and clue.id = #{id}
     and clue.name like concat('%', #{name}, '%')
     and clue.phone = #{phone}
     and clue.channel = #{channel}
     and clue.activity_id = #{activityId}
     and clue.sex = #{sex}
     and clue.age = #{age}
     and clue.weixin = #{weixin}
     and clue.qq = #{qq}
     and clue.level = #{level}
     and clue.subject = #{subject}

        and date_format(clue.create_time,'%y%m%d') >= date_format(#{params.beginCreateTime},'%y%m%d')


        and date_format(clue.create_time,'%y%m%d') <= date_format(#{params.endCreateTime},'%y%m%d')

     and clue.next_time = #{nextTime}
     and r.user_name like concat('%', #{owner}, '%')

     and clue.status = #{status}
     and clue.status in ('1','2')
    AND (r.latest = '1' OR r.id IS NULL)
    AND (r.type = '0' OR r.id IS NULL)



    AND (${params.dataScope} OR r.id IS NULL)

order by clue.create_time desc

select clue.id, clue.name, clue.phone, clue.channel, clue.activity_id, activity.code as code, activity.name as info,clue.status as clue_status, clue.create_time as clue_create_time,
r.user_name as owner,b.create_time as business_create_time,b.`status` as business_status,c.create_time contract_create_time,c.contract_order from tb_clue clue
left join  tb_activity activity on activity.id = clue.activity_id
left join tb_business b on clue.id=b.clue_id
left join tb_contract c on b.id=c.business_id
left join tb_assign_record r on clue.id=r.assign_id
and r.latest = '1' and r.type='0'

     and clue.id = #{id}
     and clue.name like concat('%', #{name}, '%')
     and clue.phone = #{phone}
     and clue.channel = #{channel}
     and clue.activity_id = #{activityId}
     and clue.sex = #{sex}
     and clue.age = #{age}
     and clue.weixin = #{weixin}
     and clue.qq = #{qq}
     and clue.level = #{level}
     and clue.subject = #{subject}

        and date_format(clue.create_time,'%y%m%d') >= date_format(#{params.beginCreateTime},'%y%m%d')


        and date_format(clue.create_time,'%y%m%d') <= date_format(#{params.endCreateTime},'%y%m%d')

     and clue.next_time = #{nextTime}
     and r.user_name like concat('%', #{owner}, '%')


order by clue.create_time desc

mapper.update:
update tb_clue
name = #{name}, phone = #{phone}, channel = #{channel}, activity_id = #{activityId}, sex = #{sex}, age = #{age}, weixin = #{weixin}, qq = #{qq}, level = #{level}, subject = #{subject}, status = #{status}, owner = #{owner}, create_time = #{createTime}, create_by = #{createBy}, assign_by = #{assignBy}, assign_time = #{assignTime}, false_count = #{falseCount}, next_time = #{nextTime},
where id = #{id}

mapper.select:
SELECT sum(case when t.`status`='4' THEN 1 ELSE 0 END ) AS falseClues, (SELECT COUNT(b.id) AS total FROM `tb_clue` a LEFT JOIN `tb_business` b ON a.phone = b.phone WHERE a.activity_id=#{activityId} ) AS toBusiness, count(1) AS total from tb_clue t where channel=#{channel} and t.activity_id=#{activityId}
and date_format(create_time,'%y%m%d') >= date_format(#{params.beginCreateTime},'%y%m%d')
and date_format(create_time,'%y%m%d') <= date_format(#{params.endCreateTime},'%y%m%d')

select clue.create_by, r.dept_id, count(clue.id) as num from tb_clue clue left join tb_assign_record r on r.assign_id = clue.id and r.latest='1' and r.type ='0'
where r.`create_time` BETWEEN #{indexVo.beginCreateTime} AND #{indexVo.endCreateTime}
GROUP BY clue.create_by limit 10
