mapper.select:


     and contract_no like concat('%', #{contractNo}, '%')
    channel = #{channel}
     and phone = #{phone}
     and name like concat('%', #{name}, '%')
     and subject = #{subject}
     and course_id = #{courseId}
     and activity_id = #{activityId}
     and dept_id = #{deptId}
     and create_by = #{createBy}

        and date_format(create_time,'%y%m%d') >= date_format(#{params.beginCreateTime},'%y%m%d')


        and date_format(create_time,'%y%m%d') <= date_format(#{params.endCreateTime},'%y%m%d')


ORDER BY `create_time` DESC

mapper.update:
update tb_contract
contract_no = #{contractNo}, phone = #{phone}, name = #{name}, subject = #{subject}, channel = #{channel}, course_id = #{courseId}, activity_id = #{activityId}, status = #{status}, create_by = #{createBy}, create_time = #{createTime}, contract_order = #{order}, file_name = #{fileName}, discount_type = #{discountType}, course_price = #{coursePrice},
where id = #{id}

mapper.select:
select count(1) as customersNum, sum(contract_order) as amount, sum(course_price-contract_order) as cost from tb_contract where channel=#{channel} and activity_id=#{activityId}
and date_format(create_time,'%y-%m-%d') >= date_format(#{params.beginCreateTime},'%y-%m-%d')
and date_format(create_time,'%y-%m-%d') <= date_format(#{params.endCreateTime},'%y-%m-%d')
